function getCarousel() {
    $.ajax({
        type: "get",
        url: "/get-slider",
        success: function (a) {
            var e = JSON.parse(a),
                r = (e.length, "");
            $(e).each(function (a, e) {
                (r += "<div>"),
                    "" != e.slider_url ? (r += '<a href="' + e.slider_url + '">') : (r += '<a href="#">'),
                    (r += '<picture class="w-100">'),
                    (r += '<source media="(max-width:767px)" data-lazy="' + e.slider + '" srcset="' + e.slider + '"  alt="">'),
                    (r += '<img src="' + e.slider + '" data-lazy="' + e.slider + '"  alt="">'),
                    (r += "</picture>"),
                    (r += "</div>");
            }),
                $("#main-image .sl-main-image").html(r),
                $("#main-image .sl-main-image").slick({
                    dots: !0,
                    arrows: !0,
                    centerMode: !0,
                    centerPadding: "170px",
                    slidesToShow: 1,
                    autoplay: !0,
                    autoplaySpeed: 5e3,
                    responsive: [
                        { breakpoint: 992, settings: { centerPadding: "68px" } },
                        { breakpoint: 767, settings: { centerPadding: "30px", arrows: !0, centerMode: !1 } },
                    ],
                });
        },
    });
}
