function setTimerDiscount(a) {
    var e = moment().add(a, "m").toDate(),
        r = new Date(e).getTime(),
        i = setInterval(function () {
            var a = new Date().getTime(),
                e = r - a,
                s = (Math.floor(e / 864e5), Math.floor((e % 864e5) / 36e5)),
                t = Math.floor((e % 36e5) / 6e4),
                o = Math.floor((e % 6e4) / 1e3);
            $(".discount-timer .hour").html(("0" + s).slice(-2)),
                $(".discount-timer .minute").html(("0" + t).slice(-2)),
                $(".discount-timer .second").html(("0" + o).slice(-2)),
                e < 0 && (clearInterval(i), $(".discount-timer .hour").html("00"), $(".discount-timer .minute").html("00"), $(".discount-timer .second").html("00"), $(".container-discount-timer").hide());
        }, 1e3);
}
